// this is the component that renders the chat window and the input box
// the bot will reply with the same message back to the user

import React, { useState } from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { arrayMove, SortableContext } from '@dnd-kit/sortable';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import MessageBubble from './MessageBubble';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSendMessage = () => {
    if (inputValue.trim() === '') return;

    const newMessage = {
      id: Date.now().toString(),
      text: inputValue,
      sender: 'user',
    };

    setMessages((prevMessages) => [
      ...prevMessages,
      { ...newMessage, order: prevMessages.length + 1 },
    ]);
    setInputValue('');

    // Simulate bot response
    setTimeout(() => {
      const botReply = {
        id: (Date.now() + 1).toString(),
        text: `Bot reply to: ${inputValue}`,
        sender: 'bot',
      };
      setMessages((prevMessages) => [
        ...prevMessages,
        { ...botReply, order: prevMessages.length + 1 },
      ]);
    }, 1000);
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;

    if (!over || !active) {
      return;
    }

    const overElement = document.getElementById(over.id);
    if (!overElement) {
      return;
    }

    // Get the bounding rectangle of the hovered element
    const overRect = overElement.getBoundingClientRect();
    const overMiddleY = (overRect.bottom - overRect.top) / 2;

    // Get the current mouse position during the drag
    const clientY = event.delta.y; // Dnd Kit typically tracks deltas during drag events

    const dragId = active.id;
    const hoverId = over.id;

    if (dragId !== hoverId) {
      const oldIndex = messages.findIndex((msg) => msg.id === dragId);
      const newIndex = messages.findIndex((msg) => msg.id === hoverId);

      // Only reorder if the mouse is beyond the midpoint of the hovered element
      if (clientY < overRect.top + overMiddleY && oldIndex < newIndex) {
        return;
      }
      if (clientY > overRect.top + overMiddleY && oldIndex > newIndex) {
        return;
      }

      const newMessages = arrayMove(messages, oldIndex, newIndex);

      // Reorder the messages and update the order numbers
      const reorderedMessages = newMessages.map((msg, index) => ({
        ...msg,
        order: index + 1,
      }));

      setMessages(reorderedMessages);
    }
  };

  return (
    <div className="chat-box">
      <DndContext collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext items={messages}>
          <div className="messages-container">
            {messages.map((message) => (
              <SortableMessageBubble key={message.id} message={message} />
            ))}
          </div>
        </SortableContext>
      </DndContext>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type your message..."
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatBox;

const SortableMessageBubble = ({ message }) => {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: message.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <MessageBubble message={message} />
    </div>
  );
};
