import React, { useState } from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { SortableContext, arrayMove } from '@dnd-kit/sortable';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import MessageBubble from './MessageBubble';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');


  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSendMessage = () => {
    if (inputValue.trim() === '') return;

    const newMessage = {
      id: Date.now().toString(),
      text: inputValue,
      sender: 'user',
    };

    setMessages((prevMessages) => [
      ...prevMessages,
      { ...newMessage, order: prevMessages.length + 1 },
    ]);
    setInputValue('');

    // Simulate bot response
    setTimeout(() => {
      const botReply = {
        id: (Date.now() + 1).toString(),
        text: `Hello ${inputValue}`,
        sender: 'bot',
      };
      setMessages((prevMessages) => [
        ...prevMessages,
        { ...botReply, order: prevMessages.length + 1 },
      ]);
    }, 1000);
  };

  ////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  const handleDragEnd = (event) => {
    const { active, over } = event;

    if (!over || active.id === over.id) {
      return;
    }

    const activeIndex = messages.findIndex((msg) => msg.id === active.id);
    const overIndex = messages.findIndex((msg) => msg.id === over.id);

    // Determine if we're dragging upwards or downwards
    const isDraggingDown = activeIndex < overIndex;

    // If dragging downwards, merge when dropping on the bottom half of the target
    // If dragging upwards, merge when dropping on the top half of the target
    const mergeTreshold = 0.5;

    const isOverThreshold = over.rect.top + over.rect.height * mergeTreshold > active.rect.current.translated.top;

    let reorderedMessages;

    if (isOverThreshold) {
      // Merge messages
      const activeMessage = messages[activeIndex];
      const overMessage = messages[overIndex];
      const mergedMessage = {
        ...overMessage,
        text: `${overMessage.text} ${activeMessage.text}`,
      };

      reorderedMessages = messages.filter((msg) => msg.id !== active.id).map((msg) => {
        if (msg.id === over.id) {
          return mergedMessage;
        }
        return msg;
      });
    } else {
      // Simple reorder
      reorderedMessages = arrayMove(messages, activeIndex, overIndex);
    }

    // Update message order
    reorderedMessages = reorderedMessages.map((msg, index) => ({
      ...msg,
      order: index + 1,
    }));

    setMessages(reorderedMessages);
  };

  ////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////



  return (
    <div className="chat-box">
      <DndContext collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext items={messages}>
          <div className="messages-container">
            {messages.map((message) => (
              <SortableMessageBubble key={message.id} message={message} />
            ))}
          </div>
        </SortableContext>
      </DndContext>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type your message..."
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatBox;

const SortableMessageBubble = ({ message }) => {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: message.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <MessageBubble message={message} />
    </div>
  );
};
