import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import MessageBubble from './MessageBubble';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSendMessage = () => {
    if (inputValue.trim() === '') return;

    const newMessage = {
      id: Date.now().toString(),
      text: inputValue,
      sender: 'user',
    };

    setMessages([...messages, newMessage]);
    setInputValue('');

    // Simulate bot response
    setTimeout(() => {
      const botReply = {
        id: (Date.now() + 1).toString(),
        text: `Bot reply to: ${inputValue}`,
        sender: 'bot',
      };
      setMessages((prevMessages) => [...prevMessages, botReply]);
    }, 1000);
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;

    const newMessages = Array.from(messages);
    const [reorderedMessage] = newMessages.splice(result.source.index, 1);
    newMessages.splice(result.destination.index, 0, reorderedMessage);

    setMessages(newMessages);
  };

  return (
    <div className="chat-box">
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="messages">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef} className="messages-container">
              {messages.map((message, index) => (
                <Draggable key={message.id} draggableId={message.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                    >
                      <MessageBubble message={message} />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type your message..."
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatBox;
