import React from 'react';

const MessageBubble = ({ message, isActive, isOver, isDragging }) => {
  let bubbleClass = message.sender === 'user' 
    ? 'user-message' 
    : message.sender === 'bot' 
      ? 'bot-message' 
      : 'merged-message';

  // Only apply the `over-message` class if `isOver` is true and the current message is not the one being dragged (`!isDragging`)
  const dropzoneClass = isOver && !isDragging ? 'over-message' : '';

  // Add a red background when the message is being dragged
  const draggingClass = isDragging ? 'dragging' : '';

  return (
    <div className={`message-bubble ${bubbleClass} ${dropzoneClass} ${draggingClass}`}>
      {message.text}
    </div>
  );
};

export default MessageBubble;
