import React, { useState, useEffect } from 'react';
import { DndContext, closestCenter } from '@dnd-kit/core';
import { arrayMove, SortableContext } from '@dnd-kit/sortable';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import MessageBubble from './MessageBubble';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  // List of random bot replies
  const botReplies = [
    "I see. That's interesting.",
    "Can you tell me more about that?",
    "Okay, got it!"
  ];

  // Initialize the chat with a bot message when the component mounts
  useEffect(() => {
    const initialMessage = {
      id: Date.now().toString(),
      text: 'Hello, how can I help you today?',
      sender: 'bot',
      order: 1,
    };
    setMessages([initialMessage]);
  }, []);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSendMessage = () => {
    if (inputValue.trim() === '') return;

    const newUserMessage = {
      id: Date.now().toString(),
      text: inputValue,
      sender: 'user',
      order: messages.length + 1,
    };

    setMessages((prevMessages) => [
      ...prevMessages,
      { ...newUserMessage, order: prevMessages.length + 1 },
    ]);
    setInputValue('');

    // Simulate bot response with a random reply
    setTimeout(() => {
      const randomReply = botReplies[Math.floor(Math.random() * botReplies.length)];
      const botReply = {
        id: (Date.now() + 1).toString(),
        text: randomReply,
        sender: 'bot',
        order: messages.length + 2,
      };
      setMessages((prevMessages) => [
        ...prevMessages,
        { ...botReply, order: prevMessages.length + 1 },
      ]);
    }, 1000);
  };

  const handleDelete = (id) => {
    // Remove the message by filtering it out
    setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== id));
  };

  const handleMerge = (draggedId, overId) => {
    // Find the dragged message and the message it was dropped over
    const draggedMessage = messages.find((msg) => msg.id === draggedId);
    const overMessage = messages.find((msg) => msg.id === overId);

    // Merge the text content
    const mergedMessage = {
      ...overMessage,
      text: `${overMessage.text} ${draggedMessage.text}`, // Combine texts
    };

    // Update messages: remove draggedMessage, update overMessage with merged text
    setMessages((prevMessages) =>
      prevMessages
        .filter((msg) => msg.id !== draggedId) // Remove dragged message
        .map((msg) => (msg.id === overId ? mergedMessage : msg)) // Replace overMessage with merged message
    );
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;
    if (!over) return;

    if (active.id !== over.id) {
      handleMerge(active.id, over.id); // Merge messages if dropped on another
    }
  };

  return (
    <div className="chat-box">
      <DndContext collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext items={messages}>
          <div className="messages-container">
            {messages.map((message) => (
              <SortableMessageBubble
                key={message.id}
                message={message}
                onDelete={() => handleDelete(message.id)}
              />
            ))}
          </div>
        </SortableContext>
      </DndContext>
      <div className="input-container">
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Type your message..."
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatBox;

const SortableMessageBubble = ({ message, onDelete }) => {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: message.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  return (
    <div ref={setNodeRef} style={style} {...attributes} {...listeners} className="message-bubble">
      <MessageBubble message={message} />
      <button onClick={onDelete} className="delete-button">Delete</button>
    </div>
  );
};
